import React, { useState, useEffect } from "react";
import { SmileData } from "./types";

function App() {
  const [isCapturing, setIsCapturing] = useState(false);
  const [smileData, setSmileData] = useState<SmileData | null>(null);

  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isCapturing) {
      interval = setInterval(() => {
        fetch("/api/smile")
          .then((res) => res.json())
          .then((data: SmileData) => {
            if (!("error" in data)) {
              setSmileData(data);
              // Stop capturing after a smile is detected
              setIsCapturing(false);
            }
          })
          .catch(() => {
            // Silently ignore errors if no smile is detected
          });
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [isCapturing]);

  return (
    <div className="container py-4">
      <h1 className="text-center mb-4">Smile Detector App</h1>
      <div className="text-center mb-3">
        <button
          className={`btn ${isCapturing ? "btn-danger" : "btn-primary"}`}
          onClick={() => setIsCapturing(!isCapturing)}
        >
          {isCapturing ? "Stop Capturing" : "Start Capturing"}
        </button>
      </div>
      {smileData && smileData.image ? (
        <div className="row">
          {/* Left Column: Captured Image */}
          <div className="col-md-6 d-flex align-items-center justify-content-center">
            <img
              src={smileData.image}
              alt="Captured Smile"
              className="img-fluid"
              style={{ maxWidth: "100%" }}
            />
          </div>
          {/* Right Column: Smile Coordinates */}
          <div className="col-md-6 d-flex align-items-center justify-content-center">
            <div>
              <h3>Smile Coordinates</h3>
              <pre className="text-center">
                {JSON.stringify(smileData.coordinates, null, 2)}
              </pre>
            </div>
          </div>
        </div>
      ) : (
        // Optionally, show the live video feed or a placeholder if no smile has been detected yet.
        <p className="text-center">No smile detected yet.</p>
      )}
    </div>
  );
}

export default App;
